import base64
import io
from PIL import Image

def base64_to_png(base64_string, output_path):
    # Giải mã chuỗi Base64 thành dữ liệu byte
    image_bytes = base64.b64decode(base64_string)

    # Chuyển đổi dữ liệu byte thành đối tượng hình ảnh
    image = Image.open(io.BytesIO(image_bytes))

    # Lưu hình ảnh dưới định dạng PNG
    image.save(output_path, 'PNG')

# Chuỗi Base64 đầu vào
base64_string = ""

# Chuyển đổi Base64 sang PNG
base64_to_png(base64_string, "test.png")